class ClassComponent extends react.Component{
   constructor(){
      super()
     this.state={
      name:"Naresh",
      count: 0,
      isBoy:true,
      isGirl:false,
     
     }
   }
    handleName =()=>{
      this.setState({
         count:this.state.count+1
      })
    } 
   render(){
      if(this.state.isBoy){
         return <div>This is boy</div>
      }else if(this.state.isGirl){
         return <div>This is Girl</div>
