import react,{Component} from "react";

class ClassComponent extends react.Component{
   constructor(){
      super()
     this.state={
      name:"Naresh",
      count: 0,
      isBoy:true,
      isGirl:false,
     
     }
   }
    handleName =()=>{
      this.setState({
         count:this.state.count+1
      })
    } 
   render(){
      return(
         <div>
{this.state.count}
<button onClick={this.handleName}>Change count</button>
         </div>
      )
   }

      
   
}
export default ClassComponent
