import axios from "axios";
import React, { useEffect, useState } from "react";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import './Dashboard.css';

export default function Dashboard() {

   const [items, setItems] = useState([]);
   const [category, setIcategory] = useState([]);
   const [subCategories, setsubCategories] = useState([]);
   useEffect(() => {
      getItemData();
   }, [])
   const getItemData = async () => {
      const itemUrl = 'http://localhost:8081/v1/users/items';
      const categoryUrl = 'http://localhost:8081/v1/users/items';
      const subCategoryUrl = 'http://localhost:8081/v1/users/items';
      try {
         const itemRes = await axios.get(itemUrl);
         setItems(itemRes.data);
      } catch (error) {
         console.log(error)
      }


   }
   return (
      <>
         <section className="bg-transparent">
            <div className="container">
               {/* <button onClick={() => setshow(true)} class="nav-item nav-link" href="#">Login</button>
                        <button onClick={() => setshow(false)} class="nav-item nav-link" >SignUp</button> */}
               <Navbar className="bg-transparent headerMargin">
                  <Container fluid className="bannerHeight">
                     <Navbar.Brand> <img src="/images/Rome-logo.png" alt="" /></Navbar.Brand>
                     <Navbar.Toggle aria-controls="navbarScroll" />
                     <Navbar.Collapse id="navbarScroll">
                        <Nav
                           className="ms-auto "
                           style={{ maxHeight: '100px' }}
                           navbarScroll
                        >



                        </Nav>

                     </Navbar.Collapse>
                  </Container>
               </Navbar>
            </div>
         </section>
         <section className="bg-dark pt-5 pb-5">
            <div className="container">
               <div className="filterDiv pb-5">
                  <h3 className="text-light">
                     Filter
                  </h3>
                  <div className="categoey">
                     <select class="form-select" aria-label="Default select example">
                        <option >Category</option>
                        <option value="1">One</option>
                        {

                        }
                     </select>
                  </div>
               </div>
               <div className="itemdev row column-gap-3 row-gap-4">
                  {items.map((item) => {
                     return (<>
                        <div className="item ">

                           <img src={item.itemUrl} alt="" />
                           <h5>{item.itemName}</h5>
                           <p>
                              <span className="itemPtrice">Price : </span>₹{item.itemPrice}
                           </p>
                        </div>
                     </>)
                  })}
               </div>
            </div>
         </section>
      </>
   )

}
