import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { useNavigate } from "react-router-dom";
import './Cart.css';

const Cart = ({ cartItems, onRemoveFromCart, onIncreaseQuantity, onDecreaseQuantity }) => {
  // Calculate total items and total price
  const totalItems = cartItems.reduce((total, item) => total + item.quantity, 0);
  const totalPrice = cartItems.reduce((total, item) => total + item.itemPrice * item.quantity, 0);
  const navigate = useNavigate();
  return (
    <>
      <section className="bg-transparent p-0
          navbar-bg">
        <div className="container">

          <Navbar className="bg-transparent headerMargin">
            <Container fluid className="bannerHeight">
              <Navbar.Brand> <img src="/images/Rome-logo.png" alt="" /></Navbar.Brand>
              <Navbar.Toggle aria-controls="navbarScroll" />
              <Navbar.Collapse id="navbarScroll">
                <Nav
                  className="ms-auto "
                  style={{ maxHeight: '100px' }}
                  navbarScroll
                >
                  <Nav className="me-auto">
                    <Nav.Link onClick={() => navigate('/success')}>Home</Nav.Link>
                    {/* <Nav.Link href="#features">Features</Nav.Link>
                              <Nav.Link href="#pricing">Pricing</Nav.Link> */}
                    <Nav.Link onClick={() => navigate('/cart')} >cart</Nav.Link>
                  </Nav>
                </Nav>

              </Navbar.Collapse>
            </Container>
          </Navbar>
        </div>
      </section>
      <div>
        <h2>Shopping Cart</h2>
        {/* <p>Items in cart: {totalItems}</p> */}

        <div>
          <table>
            <thead>
              <tr>
                <td>Item</td>
                <td>Item Price</td>
                <td>Quantity</td>
                <td>Amout</td>
              </tr>
            </thead>

            {cartItems.map((item) => (
              <tr key={item.itemId}>
                <td> {item.itemName}</td>
                <td>₹{item.itemPrice}</td>
                <td>{item.quantity}</td>
                <td>{item.itemPrice * item.quantity}</td>
                {/* -  x {' '} */}
                
                <td className="cart-buttons">
                  <button onClick={() => onIncreaseQuantity(item)}>+</button>
                  <button onClick={() => onDecreaseQuantity(item)}>-</button>
                  <button onClick={() => onRemoveFromCart(item.itemId)}>Remove</button>
                </td>
              </tr>
            ))}
          </table>
        </div>
        <p>Total Price: ₹{totalPrice.toFixed(2)}</p>
      </div>
    </>

  );
};

export default Cart;
